    def get_dates(self):
        today = datetime.now()
        day2 = today + timedelta(days=1)
        day3 = today + timedelta(days=2)
        day4 = today + timedelta(days=3)
        date1 = today.strftime("%d.%m.%Y")
        date2 = day2.strftime("%d.%m.%Y")
        date3 = day3.strftime("%d.%m.%Y")
        date4 = day4.strftime("%d.%m.%Y")
        self.day1_date.setText(date1)
        self.day2_date.setText(date2)
        self.day3_date.setText(date3)
        self.day4_date.setText(date4)

    #TO DO: Rewrite this to change images according to weather type
    def get_weather_symbol(self,i,j,k):
        wthr = self.api['list'][i]['weather'][0]['main']
        if wthr == "Clouds":
            self.images[j][k].setPixmap(QtGui.QPixmap(":/newPrefix/cloud.png"))
        elif wthr == "Rain":
            self.images[j][k].setPixmap(QtGui.QPixmap(":/newPrefix/rain.png"))
        elif wthr == "Snow":
            self.images[j][k].setPixmap(QtGui.QPixmap(":/newPrefix/snowflake.png"))
        elif wthr == "Clear":
            self.images[j][k].setPixmap(QtGui.QPixmap(":/newPrefix/sunny.png"))



    def output_data(self):
        self.day1_labels = [self.day1_t2,self.day1_t3,self.day1_t4,self.day1_t5,self.day1_t6,self.day1_t7,self.day1_t8]
        self.day2_labels = [self.day2_t1,self.day2_t2,self.day2_t3,self.day2_t4,self.day2_t5,self.day2_t6,self.day2_t7,self.day2_t8]
        self.day3_labels = [self.day3_t1,self.day3_t2,self.day3_t3,self.day3_t4,self.day3_t5,self.day3_t6,self.day3_t7,self.day3_t8]
        self.day4_labels = [self.day4_t1,self.day4_t2,self.day4_t3,self.day4_t4,self.day4_t5,self.day4_t6,self.day4_t7,self.day4_t8]
        self.labels = [self.day1_labels,self.day2_labels,self.day3_labels,self.day4_labels]
        self.day1_images = [self.day1_i1,self.day1_i2,self.day1_i3,self.day1_i4,self.day1_i5,self.day1_i6,self.day1_i7,self.day1_i8]
        self.day2_images = [self.day2_i1,self.day2_i2,self.day2_i3,self.day2_i4,self.day2_i5,self.day2_i6,self.day2_i7,self.day2_i8]
        self.day3_images = [self.day3_i1,self.day3_i2,self.day3_i3,self.day3_i4,self.day3_i5,self.day3_i6,self.day3_i7,self.day3_i8]
        self.day4_images = [self.day4_i1,self.day4_i2,self.day4_i3,self.day4_i4,self.day4_i5,self.day4_i6,self.day4_i7,self.day4_i8]
        self.images = [self.day1_images,self.day2_images,self.day3_images,self.day4_images]
        self.day1_t1.setText("Now: "+ str(int(self.api['list'][0]['main']['temp']))+ "°C ")
        self.get_weather_symbol(0,0,0)
        i=1
        for j, day in enumerate(self.labels):
            for k, label in enumerate(day):
                curr_temp = self.api['list'][i]['main']['temp']
                curr_output = self.api['list'][i]['dt_txt'][11:16]
                label.setText(str(curr_output) +"       " +  str(int(curr_temp))+" °C ")
                self.get_weather_symbol(i,j,k)
                i+=1


    def get_data(self):
        api_file = open('config.cfg','r')
        api_key = api_file.readline().rstrip()
        api_file.close()


        city_entry= str(self.lineEdit.text())
        api_request = requests.get("https://api.openweathermap.org/data/2.5/forecast?q="
                                   + city_entry+ "&units=metric&appid="+api_key)

        self.api = json.loads(api_request.content)

        if api_request:
            self.output_data()
            self.label_error.setText("")
        else:
            self.label_error.setText("Invalid city! Please check if you spelled your city correctly!")

